import{_ as t,c as a,o as s,ag as o}from"./chunks/framework.Ds6Eueu6.js";const h=JSON.parse('{"title":"ALTER STATISTICS","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/ALTER_STATISTICS.md","filePath":"docs/7x/ref_guide/sql_commands/ALTER_STATISTICS.md"}'),n={name:"docs/7x/ref_guide/sql_commands/ALTER_STATISTICS.md"};function i(c,e,r,l,d,T){return s(),a("div",null,e[0]||(e[0]=[o(`<h1 id="alter-statistics" tabindex="-1">ALTER STATISTICS <a class="header-anchor" href="#alter-statistics" aria-label="Permalink to &quot;ALTER STATISTICS&quot;">​</a></h1><p>Changes the definition of an extended statistics object.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ALTER STATISTICS &lt;name&gt; RENAME TO &lt;new_name&gt;</span></span>
<span class="line"><span></span></span>
<span class="line"><span>ALTER STATISTICS &lt;name&gt; OWNER TO { &lt;new_owner&gt; | CURRENT_USER | SESSION_USER }</span></span>
<span class="line"><span></span></span>
<span class="line"><span>ALTER STATISTICS &lt;name&gt; SET SCHEMA &lt;new_schema&gt;</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>ALTER STATISTICS</code> changes the parameters of an existing extended statistics object. Any parameters not specifically set in the <code>ALTER STATISTICS</code> command retain their prior settings.</p><p>You must own the statistics object to use <code>ALTER STATISTICS</code>. To change a statistics object&#39;s schema, you must also have <code>CREATE</code> privilege on the new schema. To alter the owner, you must also be a direct or indirect member of the new owning role, and that role must have <code>CREATE</code> privilege on the statistics object&#39;s schema. (These restrictions enforce that altering the owner doesn&#39;t do anything you couldn&#39;t do by dropping and recreating the statistics object. However, a superuser can alter ownership of any statistics object anyway.)</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>name : The name (optionally schema-qualified) of the statistics object to be altered.</p><p>new_owner : The user name of the new owner of the statistics object.</p><p>new_name : The new name for the statistics object.</p><p>new_schema : The new schema for the statistics object.</p><h2 id="compatibility" tabindex="-1"><a id="section6"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p>There is no <code>ALTER STATISTICS</code> statement in the SQL standard.</p><h2 id="see-also" tabindex="-1"><a id="section7"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./CREATE_STATISTICS.html">CREATE STATISTICS</a>, <a href="./DROP_STATISTICS.html">DROP STATISTICS</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>`,17)]))}const S=t(n,[["render",i]]);export{h as __pageData,S as default};
