import{_ as a,c as t,o as n,ag as s}from"./chunks/framework.Ds6Eueu6.js";const T=JSON.parse('{"title":"ALTER TEXT SEARCH CONFIGURATION","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/ALTER_TEXT_SEARCH_CONFIGURATION.md","filePath":"docs/7x/ref_guide/sql_commands/ALTER_TEXT_SEARCH_CONFIGURATION.md"}'),o={name:"docs/7x/ref_guide/sql_commands/ALTER_TEXT_SEARCH_CONFIGURATION.md"};function i(p,e,r,c,l,d){return n(),t("div",null,e[0]||(e[0]=[s(`<h1 id="alter-text-search-configuration" tabindex="-1">ALTER TEXT SEARCH CONFIGURATION <a class="header-anchor" href="#alter-text-search-configuration" aria-label="Permalink to &quot;ALTER TEXT SEARCH CONFIGURATION&quot;">​</a></h1><p>Changes the definition of a text search configuration.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ALTER TEXT SEARCH CONFIGURATION &lt;name&gt;</span></span>
<span class="line"><span>    ADD MAPPING FOR &lt;token_type&gt; [, ... ] WITH &lt;dictionary_name&gt; [, ... ]</span></span>
<span class="line"><span>ALTER TEXT SEARCH CONFIGURATION &lt;name&gt;</span></span>
<span class="line"><span>    ALTER MAPPING FOR &lt;token_type&gt; [, ... ] WITH &lt;dictionary_name&gt; [, ... ]</span></span>
<span class="line"><span>ALTER TEXT SEARCH CONFIGURATION &lt;name&gt;</span></span>
<span class="line"><span>    ALTER MAPPING REPLACE &lt;old_dictionary&gt; WITH &lt;new_dictionary&gt;</span></span>
<span class="line"><span>ALTER TEXT SEARCH CONFIGURATION &lt;name&gt;</span></span>
<span class="line"><span>    ALTER MAPPING FOR &lt;token_type&gt; [, ... ] REPLACE &lt;old_dictionary&gt; WITH &lt;new_dictionary&gt;</span></span>
<span class="line"><span>ALTER TEXT SEARCH CONFIGURATION &lt;name&gt;</span></span>
<span class="line"><span>    DROP MAPPING [ IF EXISTS ] FOR &lt;token_type&gt; [, ... ]</span></span>
<span class="line"><span>ALTER TEXT SEARCH CONFIGURATION &lt;name&gt; RENAME TO &lt;new_name&gt;</span></span>
<span class="line"><span>ALTER TEXT SEARCH CONFIGURATION &lt;name&gt; OWNER TO { &lt;new_owner&gt; | CURRENT_USER | SESSION_USER }</span></span>
<span class="line"><span>ALTER TEXT SEARCH CONFIGURATION &lt;name&gt; SET SCHEMA &lt;new_schema&gt;</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>ALTER TEXT SEARCH CONFIGURATION</code> changes the definition of a text search configuration. You can modify its mappings from token types to dictionaries, or change the configuration&#39;s name or owner.</p><p>You must be the owner of the configuration to use <code>ALTER TEXT SEARCH CONFIGURATION</code>.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>name : The name (optionally schema-qualified) of an existing text search configuration.</p><p>token_type : The name of a token type that is emitted by the configuration&#39;s parser.</p><p>dictionary_name : The name of a text search dictionary to be consulted for the specified token type(s). If multiple dictionaries are listed, they are consulted in the specified order.</p><p>old_dictionary : The name of a text search dictionary to be replaced in the mapping.</p><p>new_dictionary : The name of a text search dictionary to be substituted for old_dictionary.</p><p>new_name : The new name of the text search configuration.</p><p>new_owner : The new owner of the text search configuration.</p><p>new_schema : The new schema for the text search configuration.</p><p>The <code>ADD MAPPING FOR</code> form installs a list of dictionaries to be consulted for the specified token type(s); it is an error if there is already a mapping for any of the token types. The <code>ALTER MAPPING FOR</code> form does the same, but first removing any existing mapping for those token types. The <code>ALTER MAPPING REPLACE</code> forms substitute new_dictionary for old_dictionary anywhere the latter appears. This is done for only the specified token types when <code>FOR</code> appears, or for all mappings of the configuration when it doesn&#39;t. The <code>DROP MAPPING</code> form removes all dictionaries for the specified token type(s), causing tokens of those types to be ignored by the text search configuration. It is an error if there is no mapping for the token types, unless <code>IF EXISTS</code> appears.</p><h2 id="examples" tabindex="-1"><a id="section5"></a>Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;&lt;a id=&quot;section5&quot;&gt;&lt;/a&gt;Examples&quot;">​</a></h2><p>The following example replaces the <code>english</code> dictionary with the <code>swedish</code> dictionary anywhere that <code>english</code> is used within <code>my_config</code>.</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ALTER TEXT SEARCH CONFIGURATION my_config</span></span>
<span class="line"><span>  ALTER MAPPING REPLACE english WITH swedish;</span></span></code></pre></div><h2 id="compatibility" tabindex="-1"><a id="section6"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p>There is no <code>ALTER TEXT SEARCH CONFIGURATION</code> statement in the SQL standard.</p><h2 id="see-also" tabindex="-1"><a id="section7"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./CREATE_TEXT_SEARCH_CONFIGURATION.html">CREATE TEXT SEARCH CONFIGURATION</a>, <a href="./DROP_TEXT_SEARCH_CONFIGURATION.html">DROP TEXT SEARCH CONFIGURATION</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>`,25)]))}const E=a(o,[["render",i]]);export{T as __pageData,E as default};
