import{_ as a,c as t,o,ag as s}from"./chunks/framework.Ds6Eueu6.js";const u=JSON.parse('{"title":"ALTER USER MAPPING","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/ALTER_USER_MAPPING.md","filePath":"docs/7x/ref_guide/sql_commands/ALTER_USER_MAPPING.md"}'),i={name:"docs/7x/ref_guide/sql_commands/ALTER_USER_MAPPING.md"};function n(r,e,d,p,c,l){return o(),t("div",null,e[0]||(e[0]=[s(`<h1 id="alter-user-mapping" tabindex="-1">ALTER USER MAPPING <a class="header-anchor" href="#alter-user-mapping" aria-label="Permalink to &quot;ALTER USER MAPPING&quot;">​</a></h1><p>Changes the definition of a user mapping for a foreign server.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ALTER USER MAPPING FOR { &lt;user_name&gt; | USER | CURRENT_USER | SESSION_USER | PUBLIC }</span></span>
<span class="line"><span>    SERVER &lt;server_name&gt;</span></span>
<span class="line"><span>    OPTIONS ( [ ADD | SET | DROP ] &lt;option&gt; [&#39;&lt;value&gt;&#39;] [, ... ] )</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>ALTER USER MAPPING</code> changes the definition of a user mapping for a foreign server.</p><p>The owner of a foreign server can alter user mappings for that server for any user. Also, a user granted <code>USAGE</code> privilege on the server can alter a user mapping for their own user name.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>user_name : User name of the mapping. <code>CURRENT_USER</code> and <code>USER</code> match the name of the current user. <code>PUBLIC</code> is used to match all present and future user names in the system.</p><p>server_name : Server name of the user mapping.</p><p>OPTIONS ( [ ADD | SET | DROP ] option [&#39;value&#39;] [, ... ] ) : Change options for the user mapping. The new options override any previously specified options. <code>ADD</code>, <code>SET</code>, and <code>DROP</code> specify the action to perform. If no operation is explicitly specified, the default operation is <code>ADD</code>. Option names must be unique. WarehousePG validates names and values using the server&#39;s foreign-data wrapper.</p><h2 id="examples" tabindex="-1"><a id="section6"></a>Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Examples&quot;">​</a></h2><p>Change the password for user mapping <code>bob</code>, server <code>foo</code>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ALTER USER MAPPING FOR bob SERVER foo OPTIONS (SET password &#39;public&#39;);</span></span></code></pre></div><h2 id="compatibility" tabindex="-1"><a id="section7"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p><code>ALTER USER MAPPING</code> conforms to ISO/IEC 9075-9 (SQL/MED). There is a subtle syntax issue: The standard omits the <code>FOR</code> key word. Since both <code>CREATE USER MAPPING</code> and <code>DROP USER MAPPING</code> use <code>FOR</code> in analogous positions, WarehousePG diverges from the standard here in the interest of consistency and interoperability.</p><h2 id="see-also" tabindex="-1"><a id="section8"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section8&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./CREATE_USER_MAPPING.html">CREATE USER MAPPING</a>, <a href="./DROP_USER_MAPPING.html">DROP USER MAPPING</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>`,19)]))}const m=a(i,[["render",n]]);export{u as __pageData,m as default};
