import{_ as e,c as s,o as a,ag as t}from"./chunks/framework.Ds6Eueu6.js";const u=JSON.parse('{"title":"CREATE CONVERSION","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/CREATE_CONVERSION.md","filePath":"docs/7x/ref_guide/sql_commands/CREATE_CONVERSION.md"}'),i={name:"docs/7x/ref_guide/sql_commands/CREATE_CONVERSION.md"};function o(c,n,p,l,r,d){return a(),s("div",null,n[0]||(n[0]=[t(`<h1 id="create-conversion" tabindex="-1">CREATE CONVERSION <a class="header-anchor" href="#create-conversion" aria-label="Permalink to &quot;CREATE CONVERSION&quot;">​</a></h1><p>Defines a new encoding conversion.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>CREATE [DEFAULT] CONVERSION &lt;name&gt;</span></span>
<span class="line"><span>     FOR &lt;source_encoding&gt; TO &lt;dest_encoding&gt; FROM &lt;function_name&gt;</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>CREATE CONVERSION</code> defines a new conversion between character set encodings. Also, conversions that are marked <code>DEFAULT</code> can be used for automatic encoding conversion between client and server. For this purpose, two conversions, from encoding A to B <em>and</em> from encoding B to A, must be defined.</p><p>To create a conversion, you must have <code>EXECUTE</code> privilege on the function and <code>CREATE</code> privilege on the destination schema.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>DEFAULT : Indicates that this conversion is the default for this particular source to destination encoding. There should be only one default encoding in a schema for the encoding pair.</p><p>name : The name of the conversion. The conversion name may be schema-qualified. If it is not, the conversion is defined in the current schema. The conversion name must be unique within a schema.</p><p>source_encoding : The source encoding name.</p><p>dest_encoding : The destination encoding name.</p><p>function_name : The function used to perform the conversion. The function name may be schema-qualified. If it is not, the function will be looked up in the path. The function must have the following signature:</p><p>: \`\`\` conv_proc( integer, -- source encoding ID integer, -- destination encoding ID cstring, -- source string (null terminated C string) internal, -- destination (fill with a null terminated C string) integer -- source string length ) RETURNS void;</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span></span></span>
<span class="line"><span>## &lt;a id=&quot;section5&quot;&gt;&lt;/a&gt;Notes </span></span>
<span class="line"><span></span></span>
<span class="line"><span>Use [DROP CONVERSION](DROP_CONVERSION.html) to remove a user-defined conversion.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>The privileges required to create a conversion might change in a feature release.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Note that in this release of WarehousePG, user-defined functions used in a user-defined conversion must be defined as \`IMMUTABLE\`. Any compiled code \\(shared library files\\) for custom functions must be placed in the same location on every host in your WarehousePG cluster \\(coordinator and all segments\\). This location must also be in the \`LD_LIBRARY_PATH\` so that the server can locate the files.</span></span>
<span class="line"><span></span></span>
<span class="line"><span></span></span>
<span class="line"><span>## &lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Examples </span></span>
<span class="line"><span></span></span>
<span class="line"><span>To create a conversion from encoding \`UTF8\` to \`LATIN1\` using \`myfunc\`:</span></span></code></pre></div><p>CREATE CONVERSION myconv FOR &#39;UTF8&#39; TO &#39;LATIN1&#39; FROM myfunc;</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span></span></span>
<span class="line"><span>## &lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;Compatibility </span></span>
<span class="line"><span></span></span>
<span class="line"><span>\`CREATE CONVERSION\` is a WarehousePG extension. There is no \`CREATE CONVERSION\` statement in the SQL standard, but there is a \`CREATE TRANSLATION\` statement that is very similar in purpose and syntax.</span></span>
<span class="line"><span></span></span>
<span class="line"><span>## &lt;a id=&quot;section8&quot;&gt;&lt;/a&gt;See Also </span></span>
<span class="line"><span></span></span>
<span class="line"><span>[ALTER CONVERSION](ALTER_CONVERSION.html), [CREATE FUNCTION](CREATE_FUNCTION.html), [DROP CONVERSION](DROP_CONVERSION.html)</span></span>
<span class="line"><span></span></span>
<span class="line"><span>**Parent topic:** [SQL Commands](../sql_commands/sql_ref.html)</span></span></code></pre></div>`,17)]))}const m=e(i,[["render",o]]);export{u as __pageData,m as default};
