import{_ as a,c as t,o as i,ag as s}from"./chunks/framework.Ds6Eueu6.js";const u=JSON.parse('{"title":"CREATE TEXT SEARCH CONFIGURATION","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/CREATE_TEXT_SEARCH_CONFIGURATION.md","filePath":"docs/7x/ref_guide/sql_commands/CREATE_TEXT_SEARCH_CONFIGURATION.md"}'),o={name:"docs/7x/ref_guide/sql_commands/CREATE_TEXT_SEARCH_CONFIGURATION.md"};function n(r,e,c,l,h,d){return i(),t("div",null,e[0]||(e[0]=[s(`<h1 id="create-text-search-configuration" tabindex="-1">CREATE TEXT SEARCH CONFIGURATION <a class="header-anchor" href="#create-text-search-configuration" aria-label="Permalink to &quot;CREATE TEXT SEARCH CONFIGURATION&quot;">​</a></h1><p>Defines a new text search configuration.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>CREATE TEXT SEARCH CONFIGURATION &lt;name&gt; (</span></span>
<span class="line"><span>    PARSER = &lt;parser_name&gt; |</span></span>
<span class="line"><span>    COPY = &lt;source_config&gt;</span></span>
<span class="line"><span>)</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>CREATE TEXT SEARCH CONFIGURATION</code> creates a new text search configuration. A text search configuration specifies a text search parser that can divide a string into tokens, plus dictionaries that can be used to determine which tokens are of interest for searching.</p><p>If only the parser is specified, then the new text search configuration initially has no mappings from token types to dictionaries, and therefore will ignore all words. Subsequent <a href="./ALTER_TEXT_SEARCH_CONFIGURATION.html">ALTER TEXT SEARCH CONFIGURATION</a> commands must be used to create mappings to make the configuration useful. Alternatively, an existing text search configuration can be copied.</p><p>If a schema name is provided then the text search configuration is created in the specified schema. Otherwise it is created in the current schema.</p><p>The user who defines a text search configuration becomes its owner.</p><p>Refer to <a href="./../../admin_guide/textsearch/full-text-search.html#full-text-search">Using Full Text Search</a> for further information.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>name : The name of the text search configuration to be created. The name can be schema-qualified.</p><p>parser_name : The name of the text search parser to use for this configuration.</p><p>source_config : The name of an existing text search configuration to copy.</p><h2 id="notes" tabindex="-1"><a id="section9"></a>Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;&lt;a id=&quot;section9&quot;&gt;&lt;/a&gt;Notes&quot;">​</a></h2><p>The <code>PARSER</code> and <code>COPY</code> options are mutually exclusive, because when an existing configuration is copied, its parser selection is copied too.</p><h2 id="compatibility" tabindex="-1"><a id="section6"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p>There is no <code>CREATE TEXT SEARCH CONFIGURATION</code> statement in the SQL standard.</p><h2 id="see-also" tabindex="-1"><a id="section7"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./ALTER_TEXT_SEARCH_CONFIGURATION.html">ALTER TEXT SEARCH CONFIGURATION</a>, <a href="./DROP_TEXT_SEARCH_CONFIGURATION.html">DROP TEXT SEARCH CONFIGURATION</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>`,21)]))}const T=a(o,[["render",n]]);export{u as __pageData,T as default};
