import{_ as t,c as a,o as i,ag as s}from"./chunks/framework.Ds6Eueu6.js";const T=JSON.parse('{"title":"CREATE TEXT SEARCH TEMPLATE","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/CREATE_TEXT_SEARCH_TEMPLATE.md","filePath":"docs/7x/ref_guide/sql_commands/CREATE_TEXT_SEARCH_TEMPLATE.md"}'),n={name:"docs/7x/ref_guide/sql_commands/CREATE_TEXT_SEARCH_TEMPLATE.md"};function o(r,e,c,l,p,d){return i(),a("div",null,e[0]||(e[0]=[s(`<h1 id="create-text-search-template" tabindex="-1">CREATE TEXT SEARCH TEMPLATE <a class="header-anchor" href="#create-text-search-template" aria-label="Permalink to &quot;CREATE TEXT SEARCH TEMPLATE&quot;">​</a></h1><h2 id="description" tabindex="-1"><a id="Description"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;Description&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p>Defines a new text search template.</p><h2 id="synopsis" tabindex="-1"><a id="Synopsis"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;Synopsis&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>CREATE TEXT SEARCH TEMPLATE &lt;name&gt; (</span></span>
<span class="line"><span>    [ INIT = &lt;init_function&gt; , ]</span></span>
<span class="line"><span>    LEXIZE = &lt;lexize_function&gt;</span></span>
<span class="line"><span>)</span></span></code></pre></div><h2 id="description-1" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description-1" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>CREATE TEXT SEARCH TEMPLATE</code> creates a new text search template. Text search templates define the functions that implement text search dictionaries. A template is not useful by itself, but must be instantiated as a dictionary to be used. The dictionary typically specifies parameters to be given to the template functions.</p><p>If a schema name is given then the text search template is created in the specified schema. Otherwise it is created in the current schema.</p><p>You must be a superuser to use <code>CREATE TEXT SEARCH TEMPLATE</code>. This restriction is made because an erroneous text search template definition could confuse or even crash the server. The reason for separating templates from dictionaries is that a template encapsulates the &quot;unsafe&quot; aspects of defining a dictionary. The parameters that can be set when defining a dictionary are safe for unprivileged users to set, and so creating a dictionary need not be a privileged operation.</p><p>Refer to <a href="./../../admin_guide/textsearch/full-text-search.html#full-text-search">Using Full Text Search</a> for further information.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>name : The name of the text search template to be created. The name can be schema-qualified.</p><p>init_function : The name of the init function for the template.</p><p>lexize_function : The name of the lexize function for the template.</p><p>The function names can be schema-qualified if necessary. Argument types are not given, since the argument list for each type of function is predetermined. The lexize function is required, but the init function is optional.</p><p>The arguments can appear in any order, not only the order shown above.</p><h2 id="compatibility" tabindex="-1"><a id="section7"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p>There is no <code>CREATE TEXT SEARCH TEMPLATE</code> statement in the SQL standard.</p><h2 id="see-also" tabindex="-1"><a id="section8"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section8&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./DROP_TEXT_SEARCH_TEMPLATE.html">DROP TEXT SEARCH TEMPLATE</a>, <a href="./ALTER_TEXT_SEARCH_TEMPLATE.html">ALTER TEXT SEARCH TEMPLATE</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>`,21)]))}const E=t(n,[["render",o]]);export{T as __pageData,E as default};
