import{_ as a,c as t,o as s,ag as o}from"./chunks/framework.Ds6Eueu6.js";const u=JSON.parse('{"title":"CREATE USER MAPPING","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/CREATE_USER_MAPPING.md","filePath":"docs/7x/ref_guide/sql_commands/CREATE_USER_MAPPING.md"}'),i={name:"docs/7x/ref_guide/sql_commands/CREATE_USER_MAPPING.md"};function n(r,e,p,c,l,d){return s(),t("div",null,e[0]||(e[0]=[o(`<h1 id="create-user-mapping" tabindex="-1">CREATE USER MAPPING <a class="header-anchor" href="#create-user-mapping" aria-label="Permalink to &quot;CREATE USER MAPPING&quot;">​</a></h1><p>Defines a new mapping of a user to a foreign server.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>CREATE USER MAPPING [ IF NOT EXISTS ] FOR { &lt;user_name&gt; | USER | CURRENT_USER | PUBLIC }</span></span>
<span class="line"><span>    SERVER &lt;server_name&gt;</span></span>
<span class="line"><span>    [ OPTIONS ( &lt;option&gt; &#39;&lt;value&gt;&#39; [, ... ] ) ]</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>CREATE USER MAPPING</code> defines a mapping of a user to a foreign server. A user mapping typically encapsulates connection information that a foreign-data wrapper uses together with the information encapsulated by a foreign server to access an external data resource.</p><p>The owner of a foreign server can create user mappings for that server for any user. Also, a user can create a user mapping for their own user name if they have been granted <code>USAGE</code> privilege on the server.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>IF NOT EXISTS : Do not throw an error if a mapping of the given user to the given foreign server already exists. WarehousePG issues a notice in this case. Note that there is no guarantee that the existing user mapping is anything like the one that would have been created.</p><p>user_name : The name of an existing user that is mapped to the foreign server. <code>CURRENT_USER</code> and <code>USER</code> match the name of the current user. When <code>PUBLIC</code> is specified, WarehousePG creates a so-called public mapping that is used when no user-specific mapping is applicable.</p><p>server_name : The name of an existing server for which WarehousePG is to create the user mapping.</p><p>OPTIONS ( option &#39;value&#39; [, ... ] ) : The options for the new user mapping. The options typically define the actual user name and password of the mapping. Option names must be unique. The option names and values are specific to the server&#39;s foreign-data wrapper.</p><h2 id="examples" tabindex="-1"><a id="section6"></a>Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Examples&quot;">​</a></h2><p>Create a user mapping for user <code>bob</code>, server <code>foo</code>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>CREATE USER MAPPING FOR bob SERVER foo OPTIONS (user &#39;bob&#39;, password &#39;secret&#39;);</span></span></code></pre></div><h2 id="compatibility" tabindex="-1"><a id="section7"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p><code>CREATE USER MAPPING</code> conforms to ISO/IEC 9075-9 (SQL/MED).</p><h2 id="see-also" tabindex="-1"><a id="section8"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section8&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./ALTER_USER_MAPPING.html">ALTER USER MAPPING</a>, <a href="./DROP_USER_MAPPING.html">DROP USER MAPPING</a>, <a href="./CREATE_FOREIGN_DATA_WRAPPER.html">CREATE FOREIGN DATA WRAPPER</a>, <a href="./CREATE_SERVER.html">CREATE SERVER</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>`,20)]))}const m=a(i,[["render",n]]);export{u as __pageData,m as default};
