import{_ as e,c as t,o,ag as s}from"./chunks/framework.Ds6Eueu6.js";const m=JSON.parse('{"title":"DROP DATABASE","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/DROP_DATABASE.md","filePath":"docs/7x/ref_guide/sql_commands/DROP_DATABASE.md"}'),i={name:"docs/7x/ref_guide/sql_commands/DROP_DATABASE.md"};function n(d,a,r,c,l,h){return o(),t("div",null,a[0]||(a[0]=[s('<h1 id="drop-database" tabindex="-1">DROP DATABASE <a class="header-anchor" href="#drop-database" aria-label="Permalink to &quot;DROP DATABASE&quot;">​</a></h1><p>Removes a database.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DROP DATABASE [IF EXISTS] &lt;name&gt;</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>DROP DATABASE</code> drops a database. It removes the catalog entries for the database and deletes the directory containing the data. It can only be run by the database owner. Also, it cannot be run while you or anyone else are connected to the target database. (Connect to <code>postgres</code> or any other database to issue this command.)</p><blockquote><p><strong>Caution</strong> <code>DROP DATABASE</code> cannot be undone. Use it with care!</p></blockquote><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>IF EXISTS : Do not throw an error if the database does not exist. A notice is issued in this case.</p><p>name : The name of the database to remove.</p><h2 id="notes" tabindex="-1"><a id="section5"></a>Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;&lt;a id=&quot;section5&quot;&gt;&lt;/a&gt;Notes&quot;">​</a></h2><p><code>DROP DATABASE</code> cannot be run inside a transaction block.</p><p>This command cannot be run while connected to the target database. Thus, it might be more convenient to use the program <a href="./../../utility_guide/ref/dropdb.html">dropdb</a> instead, which is a wrapper around this command.</p><h2 id="examples" tabindex="-1"><a id="section6"></a>Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Examples&quot;">​</a></h2><p>Drop the database named <code>testdb</code>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DROP DATABASE testdb;</span></span></code></pre></div><h2 id="compatibility" tabindex="-1"><a id="section7"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p>There is no <code>DROP DATABASE</code> statement in the SQL standard.</p><h2 id="see-also" tabindex="-1"><a id="section8"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section8&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./ALTER_DATABASE.html">ALTER DATABASE</a>, <a href="./CREATE_DATABASE.html">CREATE DATABASE</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>',21)]))}const u=e(i,[["render",n]]);export{m as __pageData,u as default};
