import{_ as e,c as t,o,ag as s}from"./chunks/framework.Ds6Eueu6.js";const u=JSON.parse('{"title":"DROP LANGUAGE","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/DROP_LANGUAGE.md","filePath":"docs/7x/ref_guide/sql_commands/DROP_LANGUAGE.md"}'),i={name:"docs/7x/ref_guide/sql_commands/DROP_LANGUAGE.md"};function n(l,a,r,d,p,c){return o(),t("div",null,a[0]||(a[0]=[s('<h1 id="drop-language" tabindex="-1">DROP LANGUAGE <a class="header-anchor" href="#drop-language" aria-label="Permalink to &quot;DROP LANGUAGE&quot;">​</a></h1><p>Removes a procedural language.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DROP [PROCEDURAL] LANGUAGE [IF EXISTS] &lt;name&gt; [CASCADE | RESTRICT]</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>DROP LANGUAGE</code> removes the definition of the previously registered procedural language. You must be a superuser or owner of the language to drop a language.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>PROCEDURAL : Optional keyword - has no effect.</p><p>IF EXISTS : Do not throw an error if the language does not exist. A notice is issued in this case.</p><p>name : The name of an existing procedural language. For backward compatibility, the name may be enclosed by single quotes.</p><p>CASCADE : Automatically drop objects that depend on the language (such as functions written in that language), and in turn all objects that depend on those objects.</p><p>RESTRICT : Refuse to drop the language if any objects depend on it. This is the default.</p><h2 id="examples" tabindex="-1"><a id="section5"></a>Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;&lt;a id=&quot;section5&quot;&gt;&lt;/a&gt;Examples&quot;">​</a></h2><p>Remove the procedural language <code>plsample</code>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DROP LANGUAGE plsample;</span></span></code></pre></div><h2 id="compatibility" tabindex="-1"><a id="section6"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p>There is no <code>DROP LANGUAGE</code> statement in the SQL standard.</p><h2 id="see-also" tabindex="-1"><a id="section7"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./ALTER_LANGUAGE.html">ALTER LANGUAGE</a>, <a href="./CREATE_LANGUAGE.html">CREATE LANGUAGE</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>',20)]))}const g=e(i,[["render",n]]);export{u as __pageData,g as default};
