import{_ as a,c as t,o,ag as i}from"./chunks/framework.Ds6Eueu6.js";const m=JSON.parse('{"title":"DROP POLICY","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/DROP_POLICY.md","filePath":"docs/7x/ref_guide/sql_commands/DROP_POLICY.md"}'),s={name:"docs/7x/ref_guide/sql_commands/DROP_POLICY.md"};function l(n,e,d,r,c,p){return o(),t("div",null,e[0]||(e[0]=[i('<h1 id="drop-policy" tabindex="-1">DROP POLICY <a class="header-anchor" href="#drop-policy" aria-label="Permalink to &quot;DROP POLICY&quot;">​</a></h1><p>Removes a row-level security policy from a table.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DROP POLICY [ IF EXISTS ] &lt;name&gt; ON &lt;table_name&gt; [ CASCADE | RESTRICT ]</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>DROP POLICY</code> removes the specified policy from the table. Note that if the last policy is removed for a table and the table still has row-level security enabled via <code>ALTER TABLE</code>, then the default-deny policy will be used. <code>ALTER TABLE ... DISABLE ROW LEVEL SECURITY</code> can be used to disable row-level security for a table, whether policies for the table exist or not.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>IF EXISTS : Do not throw an error if the policy does not exist. A notice is issued in this case.</p><p>name : The name of the policy to drop.</p><p>table_name : The name (optionally schema-qualified) of the table that the policy is on.</p><p>CASCADE RESTRICT : These key words have no effect, since there are no dependencies on policies.</p><h2 id="examples" tabindex="-1"><a id="section5"></a>Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;&lt;a id=&quot;section5&quot;&gt;&lt;/a&gt;Examples&quot;">​</a></h2><p>To drop the policy called <code>p1</code> on the table named <code>my_table</code>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DROP POLICY p1 ON my_table;</span></span></code></pre></div><h2 id="compatibility" tabindex="-1"><a id="section6"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p><code>DROP POLICY</code> is a WarehousePG extension to the SQL standard.</p><h2 id="see-also" tabindex="-1"><a id="section7"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./CREATE_POLICY.html">CREATE POLICY</a>, <a href="./ALTER_POLICY.html">ALTER POLICY</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>',19)]))}const u=a(s,[["render",l]]);export{m as __pageData,u as default};
