import{_ as e,c as t,o as s,ag as i}from"./chunks/framework.Ds6Eueu6.js";const S=JSON.parse('{"title":"DROP STATISTICS","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/DROP_STATISTICS.md","filePath":"docs/7x/ref_guide/sql_commands/DROP_STATISTICS.md"}'),o={name:"docs/7x/ref_guide/sql_commands/DROP_STATISTICS.md"};function n(r,a,c,l,d,p){return s(),t("div",null,a[0]||(a[0]=[i(`<h1 id="drop-statistics" tabindex="-1">DROP STATISTICS <a class="header-anchor" href="#drop-statistics" aria-label="Permalink to &quot;DROP STATISTICS&quot;">​</a></h1><p>Removes extended statistics.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DROP STATISTICS [ IF EXISTS ] &lt;name&gt; [, ...] [ CASCADE | RESTRICT ]</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>DROP STATISTICS</code> removes statistics object(s) from the database. Only the statistics object&#39;s owner, the schema owner, or a superuser can drop a statistics object.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p><code>IF EXISTS</code> : Do not throw an error if the statistics object does not exist. WarehousePG issues a notice in this case.</p><p>name : The name (optionally schema-qualified) of the statistics object to drop.</p><p>CASCADE RESTRICT : These key words have no effect, since there are no dependencies on statistics.</p><h2 id="examples" tabindex="-1"><a id="section6"></a>Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Examples&quot;">​</a></h2><p>Destroy two statistics objects in different schemas, without failing if they do not exist:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DROP STATISTICS IF EXISTS</span></span>
<span class="line"><span>    accounting.users_uid_creation,</span></span>
<span class="line"><span>    public.grants_user_role;</span></span></code></pre></div><h2 id="compatibility" tabindex="-1"><a id="section7"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p>There is no <code>DROP STATISTICS</code> command in the SQL standard.</p><h2 id="see-also" tabindex="-1"><a id="section8"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section8&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./ALTER_STATISTICS.html">ALTER STATISTICS</a>, <a href="./CREATE_STATISTICS.html">CREATE STATISTICS</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>`,18)]))}const T=e(o,[["render",n]]);export{S as __pageData,T as default};
