import{_ as a,c as t,o as s,ag as i}from"./chunks/framework.Ds6Eueu6.js";const u=JSON.parse('{"title":"LOAD","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/LOAD.md","filePath":"docs/7x/ref_guide/sql_commands/LOAD.md"}'),o={name:"docs/7x/ref_guide/sql_commands/LOAD.md"};function r(l,e,n,d,c,h){return s(),t("div",null,e[0]||(e[0]=[i('<h1 id="load" tabindex="-1">LOAD <a class="header-anchor" href="#load" aria-label="Permalink to &quot;LOAD&quot;">​</a></h1><p>Loads or reloads a shared library file.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>LOAD &#39;&lt;filename&gt;&#39;</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p>This command loads a shared library file into the WarehousePG server address space. If the file had been loaded previously, it is first unloaded. This command is primarily useful to unload and reload a shared library file that has been changed since the server first loaded it. To make use of the shared library, function(s) in it need to be declared using the <code>CREATE FUNCTION</code> command.</p><p>The library file name is typically given as just a bare file name, which is sought in the server&#39;s library search path (set by <code>dynamic_library_path</code>). Alternatively it can be given as a full path name. In either case the platform&#39;s standard shared library file name extension may be omitted.</p><p>Note that in WarehousePG the shared library file (<code>.so</code> file) must reside in the same path location on every host in the WarehousePG cluster (coordinators, segments, and mirrors).</p><p>Non-superusers can only apply <code>LOAD</code> to library files located in <code>$libdir/plugins/</code> — the specified <code>filename</code> must begin with exactly that string. You must ensure that only “safe” libraries are installed there.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>filename : The path and file name of a shared library file. This file must exist in the same location on all hosts in your WarehousePG cluster.</p><h2 id="examples" tabindex="-1"><a id="section5"></a>Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;&lt;a id=&quot;section5&quot;&gt;&lt;/a&gt;Examples&quot;">​</a></h2><p>Load a shared library file:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>LOAD &#39;/usr/local/greenplum-db/lib/myfuncs.so&#39;;</span></span></code></pre></div><h2 id="compatibility" tabindex="-1"><a id="section6"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p><code>LOAD</code> is a WarehousePG extension.</p><h2 id="see-also" tabindex="-1"><a id="section7"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./CREATE_FUNCTION.html">CREATE FUNCTION</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>',19)]))}const m=a(o,[["render",r]]);export{u as __pageData,m as default};
