import{_ as a,c as t,o as s,ag as i}from"./chunks/framework.Ds6Eueu6.js";const u=JSON.parse('{"title":"UNLISTEN","description":"","frontmatter":{},"headers":[],"relativePath":"docs/7x/ref_guide/sql_commands/UNLISTEN.md","filePath":"docs/7x/ref_guide/sql_commands/UNLISTEN.md"}'),n={name:"docs/7x/ref_guide/sql_commands/UNLISTEN.md"};function o(l,e,r,c,d,p){return s(),t("div",null,e[0]||(e[0]=[i(`<h1 id="unlisten" tabindex="-1">UNLISTEN <a class="header-anchor" href="#unlisten" aria-label="Permalink to &quot;UNLISTEN&quot;">​</a></h1><p>Stops listening for a notification.</p><h2 id="synopsis" tabindex="-1"><a id="section2"></a>Synopsis <a class="header-anchor" href="#synopsis" aria-label="Permalink to &quot;&lt;a id=&quot;section2&quot;&gt;&lt;/a&gt;Synopsis&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>UNLISTEN { &lt;channel&gt; | * }</span></span></code></pre></div><h2 id="description" tabindex="-1"><a id="section3"></a>Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;&lt;a id=&quot;section3&quot;&gt;&lt;/a&gt;Description&quot;">​</a></h2><p><code>UNLISTEN</code> is used to remove an existing registration for <code>NOTIFY</code> events. <code>UNLISTEN</code> cancels any existing registration of the current WarehousePG session as a listener on the notification channel named channel. The special wildcard <code>*</code> cancels all listener registrations for the current session.</p><p><a href="./NOTIFY.html">NOTIFY</a> contains a more extensive discussion of the use of <code>LISTEN</code> and <code>NOTIFY</code>.</p><h2 id="parameters" tabindex="-1"><a id="section4"></a>Parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;&lt;a id=&quot;section4&quot;&gt;&lt;/a&gt;Parameters&quot;">​</a></h2><p>channel : The name of a notification channel (any identifier).</p><ul><li></li></ul><p>: All current listen registrations for this session are cleared.</p><h2 id="notes" tabindex="-1"><a id="section4a"></a>Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;&lt;a id=&quot;section4a&quot;&gt;&lt;/a&gt;Notes&quot;">​</a></h2><p>You can unlisten something you were not listening for; no warning or error will appear.</p><p>At the end of each session, <code>UNLISTEN *</code> is automatically executed.</p><p>A transaction that has executed <code>UNLISTEN</code> cannot be prepared for two-phase commit.</p><h2 id="examples" tabindex="-1"><a id="section5"></a>Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;&lt;a id=&quot;section5&quot;&gt;&lt;/a&gt;Examples&quot;">​</a></h2><p>To make a registration:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>LISTEN virtual;</span></span>
<span class="line"><span>NOTIFY virtual;</span></span>
<span class="line"><span>Asynchronous notification &quot;virtual&quot; received from server process with PID 8448.</span></span></code></pre></div><p>Once <code>UNLISTEN</code> has been executed, further <code>NOTIFY</code> messages will be ignored:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>UNLISTEN virtual;</span></span>
<span class="line"><span>NOTIFY virtual;</span></span>
<span class="line"><span>-- no NOTIFY event is received</span></span></code></pre></div><h2 id="compatibility" tabindex="-1"><a id="section6"></a>Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;&lt;a id=&quot;section6&quot;&gt;&lt;/a&gt;Compatibility&quot;">​</a></h2><p>There is no <code>UNLISTEN</code> statement in the SQL standard.</p><h2 id="see-also" tabindex="-1"><a id="section7"></a>See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;&lt;a id=&quot;section7&quot;&gt;&lt;/a&gt;See Also&quot;">​</a></h2><p><a href="./LISTEN.html">LISTEN</a>, <a href="./NOTIFY.html">NOTIFY</a></p><p><strong>Parent topic:</strong> <a href="./../sql_commands/sql_ref.html">SQL Commands</a></p>`,25)]))}const m=a(n,[["render",o]]);export{u as __pageData,m as default};
